import React, { useState, useEffect } from 'react';
import uniqid from "uniqid";
import Card from './Card';
import UKFlag from '../images/UK_flag.svg.png'
import FranceFlag from '../images/France_flag.jpg'
import DenmarkFlag from '../images/Denmark_flag.png'

function createCards() {
    let cardArray = [];
    let cards = [
        Card(UKFlag, "United Kingdom", "red", uniqid()),
        Card(FranceFlag, "France", "green", uniqid()),
    ];

    cards.forEach((card) => {
        cardArray.push(card);
    })

    return cardArray;
}

const CardGrid = () => {
    let cardArray = createCards();

    return (
        <div className='card-grid'>
            {cardArray.map((card) => {
                return (
                    <div
                        className='card'
                        key={card.id}
                        id={card.id}
                        style={card.backgroundColor}
                    >
                        <img src={card.src} alt={card.title}></img>
                        <figcaption>{card.title}</figcaption>
                    </div>
                )
            })}
        </div>
    )
}

export default CardGrid;
