import uniqid from "uniqid";

let Card = (props) => {
    const {
        card: {
            src, title, backgroundColor
        }
    } = props;

    let id = uniqid()

    let backgroundColorStyle = {
        backgroundColor: backgroundColor,
    };

    return (
        <div
            className="card"
            style={backgroundColorStyle}
        >
            <img src={src} alt = {title}></img>
            <figcaption>{title}</figcaption>
        </div >
    )
}

export default Card;
