import React, { useState, useEffect } from 'react';
import uniqid from "uniqid";
import Card from './Card';
import UKFlag from '../images/UK_flag.jpg'
import FranceFlag from '../images/UK_flag.jpg'

function createCards() {
    let cardArray = [];
    let cards = [
        Card(UKFlag, "United Kingdom", "red", uniqid()),
        Card(FranceFlag, "France", "green", uniqid())
    ];

    cards.forEach((card) => {
        cardArray.push(card);
    })

    return cardArray;
}

const CardGrid = () => {
    let cardArray = createCards();

    return (
        <div className='card-grid'>
            {cardArray.map((card) => {
                return(
                    <div className='card' key={card.id} id={card.id}>

                    </div>
                )
            })}
        </div>
    )
}

export default CardGrid;
